#import <AVFoundation/AVFoundation.h>extern "C"{    //put this function in AppDelegate    static void displayStatusChanged(CFNotificationCenterRef center,                                     void *observer,                                     CFStringRef name,                                     const void *object,                                     CFDictionaryRef userInfo) {        if (name == CFSTR("com.apple.springboard.lockcomplete")) {            [[NSUserDefaults standardUserDefaults] setBool:YES forKey:@"kDisplayStatusLocked"];            [[NSUserDefaults standardUserDefaults] synchronize];        }    }        void _SetiOSBackGroundAudio() {                AVAudioSession* session = [AVAudioSession sharedInstance];        NSError* error = nil;        [session setCategory:AVAudioSessionCategoryPlayback error:&error];        [session setActive:YES error:&error];                [[NSUserDefaults standardUserDefaults] setBool:NO forKey:@"kDisplayStatusLocked"];        [[NSUserDefaults standardUserDefaults] synchronize];                CFNotificationCenterAddObserver(CFNotificationCenterGetDarwinNotifyCenter(),                                        NULL,                                        displayStatusChanged,                                        CFSTR("com.apple.springboard.lockcomplete"),                                        NULL,                                        CFNotificationSuspensionBehaviorDeliverImmediately);                    }    void _ReSetiOSBackGroundAudio() {        AVAudioSession* session = [AVAudioSession sharedInstance];        NSError* error = nil;        [session setCategory:AVAudioSessionCategoryPlayback error:&error];        [session setActive:YES error:&error];    }        void SetTimerCount(int val1){            }        }